package com.eagle.coders.swing.core.ui.cache;

import java.util.List;

import com.eagle.coders.swing.core.ui.annotations.type.DomainPerpertiesAllowType;

/**
 * @author Anees
 *
 */
public class UIDomainExceptPropertyHolder {

	private Class domainObject;
	
//	private String policy;
	
	private DomainPerpertiesAllowType policy;
	
	private List<String> propertyList;

	/**
	 * @return the domainObject
	 */
	public Class getDomainObject() {
		return domainObject;
	}

	/**
	 * @param domainObject the domainObject to set
	 */
	public void setDomainObject(Class domainObject) {
		this.domainObject = domainObject;
	}

	/**
	 * @return the policy
	 */
	public DomainPerpertiesAllowType getPolicy() {
		return policy;
	}

	/**
	 * @param policy the policy to set
	 */
	public void setPolicy(DomainPerpertiesAllowType policy) {
		this.policy = policy;
	}

	/**
	 * @return the propertyList
	 */
	public List<String> getPropertyList() {
		return propertyList;
	}

	/**
	 * @param propertyList the propertyList to set
	 */
	public void setPropertyList(List<String> propertyList) {
		this.propertyList = propertyList;
	}
	
}
